[PokemonWeight] [varchar](50) NOT NULL,
[Gender] [varchar](50) NOT NULL, 
[Category] [varchar](50) NOT NULL,
[Ability] [int]  NULL,
[Ability2] [int] NULL,
[DexEntry] [varchar](1080) NOT NULL,	
