Create procedure sp_get_locations
(
@Location [nvarchar] (1000)
)
As
Begin
Select de.PokemonName, LocationDescription 
From Location l
inner join DexEntries de on l.PokemonNumber = de.PokemonNumber
where GameCaught = @Location
order by LocationID
End
Go
;
			
