CREATE PROCEDURE [sp_update_user]
(
@UserID			[int],

@NewUserName		[nvarchar](50),
@NewEmail			[nvarchar](50),		

@OldUserName		[nvarchar](50),
@OldEmail			[nvarchar](50)
)
AS
BEGIN
UPDATE [dbo].[vUser]
SET 	[UserName] = 	@NewUserName,						
		[Email] = 		@NewEmail
WHERE 	[UserID] =	@UserID  
AND	[UserName] = 	@OldUserName	  
AND	[Email] = 		@OldEmail
 
RETURN @@ROWCOUNT
END
GO
;

